$(document).on('keyup', '#odd_1, #odd_2', function () {

	var len1 = $('#odd_1').val().length;
	var len2 = $('#odd_2').val().length;

	var len = len1 + len2;

	if (len > 4) {

		var odd1 = $('#odd_1').val();
		var odd2 = $('#odd_2').val();

		var calc = (1 / odd1) + (1 / odd2);

		if (calc > 1) {
			$('#percent').html('<b style="color: red;">' + calc.toFixed(4) + '</b>');
		} else {
			$('#percent').text(calc.toFixed(4));
		}

	}
});

$(document).on('keyup', '#odd_1, #amt, #odd_2', function () {

	var odd1 = $('#odd_1').val();
	var odd2 = $('#odd_2').val();
	var amt = $('#amt').val();
	var calc = 1 / odd1 + 1 / odd2;

	var team1 = amt / (calc * odd1);
	var team1_win = (team1 * odd1) - amt;

	var team2 = amt / (calc * odd2);
	var team2_win = (team2 * odd2) - amt;

	var len1 = $('#odd_1').val().length;
	var len2 = $('#odd_2').val().length;

	var len = len1 + len2;

	if (len > 4) {

		if (team1_win > 1) {

			$('#bet_amt_1').text(team1.toFixed(1));
			//				$('#prof_amt_1').text(team1_win.toFixed(1));

			$('#prof_amt_1').text(parseFloat(team2_win, 10).toFixed(2).replace(/(\d)(?=(\d{3})+\.)/g, "$1,").toString());

			$('#bet_amt_2').text(team2.toFixed(1));
			//				$('#prof_amt_2').text(team2_win.toFixed(1) );

			$('#prof_amt_2').text(parseFloat(team2_win, 10).toFixed(2).replace(/(\d)(?=(\d{3})+\.)/g, "$1,").toString());

		} else {

			$('#bet_amt_1').text('0');
			//				$('#prof_amt_1').text(team1_win.toFixed(1));

			$('#prof_amt_1').text('0');

			$('#bet_amt_2').text('0');
			//				$('#prof_amt_2').text(team2_win.toFixed(1));

			$('#prof_amt_2').text('0');

		}
	}

});
